import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { Card, Icon, Avatar, Col, Typography, Row, Button } from 'antd';
import { FullscreenOutlined } from '@ant-design/icons';

const { Title } = Typography
const { Meta } = Card;

function BlogPage() {

    const [blogs, setBlogs] = useState([])
    const [show, setShow] = useState(false);

    useEffect(() => {
        axios.get('/api/blog/getBlogs')
            .then(response => {
                if (response.data.success) {
                    console.log(response.data.blogs)
                    setBlogs(response.data.blogs)
                } else {
                    alert('Couldnt get courses`s lists')
                }
            })
    }, [])

    const renderCards = blogs.map((blog, index) => {
        return <Col key={index} lg={8} md={12} xs={24}>
            <Card
                hoverable
                style={{ width: 370, marginTop: 16 }}
                actions={[
                    <Icon type="setting" key="setting" />,
                    <Icon type="edit" key="edit" />,
                    <a href={`/blog/post/${blog._id}`}> <FullscreenOutlined /></a>,
                ]}
            >
                   <Button
                                size="large"
                                htmlType="submit"
                                onClick={()=>setShow(true)}
                                disabled = {show}

                            >
                                Enroll
                        </Button>
                <Meta
                    avatar={
                        <Avatar src={blog.writer.image} />
                    }
                    title={blog.writer.name}
                    description="This is the description"
                />
                <div style={{ height: 150, overflowY: 'scroll', marginTop: 10 }}>
                    <div dangerouslySetInnerHTML={{ __html: blog.content }} />
                </div>
            </Card>
        </Col>
    })

    disabled = {show}
    return (
        <div style={{ width: '85%', margin: '3rem auto' }}>
            <Title level={2}> Courses Lists </Title>
            <Row gutter={[32, 16]}>
                {renderCards}
            </Row>
        </div>
    )
}

export default BlogPage
