import 'chonky/style/main.css';
import { FileBrowser, FileList, FileSearch, FileToolbar, ChonkyActions, FileAction, FileActionData } from 'chonky';
import React from 'react';

    
    const ChonkyDemo = () => {
    
        const folderChain = React.useMemo(
        () => [
            { id: 'xXre', name: 'My Documents' },
            { id: 'BtrE', name: 'Websites' },
        ],
        []
    );

        
        const fileActions = React.useMemo(
        () => [
            ChonkyActions.CreateFolder, // Adds a button to the toolbar
            ChonkyActions.UploadFiles, // Adds a button
            ChonkyActions.DownloadFiles, // Adds a button
            ChonkyActions.CopyFiles, // Adds a button and a shortcut: Ctrl+C
            ChonkyActions.DeleteFiles, // Adds a button and a shortcut: Delete
        ],
        []
    );

        const handleFileAction = React.useCallback(
            (FileAction, FileActionData) => {
                console.log('Action definition:', FileAction);
                console.log('Action data:', FileActionData);
            },
            []
        );

 
    const files = React.useMemo(
        () => [
            { id: '1eBr', name: 'index.html' },
            { id: 'xmFe', name: 'styles.css' },
            { id: 'vEgS', name: 'scripts.js' },
            { id: 'BVwA', name: 'favicon.ico' },
            { id: 'VsdE', name: 'robots.txt' },
        ],
        []
    );

    
 
    return (
     
        <FileBrowser files={files} folderChain={folderChain} fileActions={fileActions} onFileAction={handleFileAction} >
            <FileToolbar />
            <FileSearch />
            <FileList />
        </FileBrowser>
  
    );
};


export default ChonkyDemo;
