import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { Card, Icon, Avatar, Col, Typography } from 'antd';
import { useScrollIndicator } from 'react-use-scroll-indicator';
const { Title } = Typography

function PostPage(props) {

    const [post, setPost] = useState([])
    const postId = props.match.params.postId;
    const [state] = useScrollIndicator();

    useEffect(() => {

        const variable = { postId: postId }

        axios.post('/api/blog/getPost', variable)
            .then(response => {
                if (response.data.success) {
                    console.log(response.data.post)
                    setPost(response.data.post)
                } else {
                    alert('Couldnt get the Course')
                }
            })
    }, [])

    if (post.writer) {
        return (
            <div className="postPage" style={{ width: '80%', margin: '3rem auto' }}>
                <Title level={2}>{post.writer.name}`s Course</Title>
                <br />
                <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
                    <Title level={4}>{post.createdAt}</Title>
                </div>
                <div dangerouslySetInnerHTML={{ __html: post.content }} />

            </div>
        )
    } else {
        return (
            <React.Fragment>
            <div style={{ width: '80%', margin: '3rem auto' }}>loading...</div>
            <section>
      <div>Percent of page scrolled: {state.width}%</div>
      <article>... Main content ...</article>
    </section>
    </React.Fragment>
        )
    }

}

export default PostPage;
