import React from 'react';

import FileManager, { Permissions } from 'devextreme-react/file-manager';

import { fileItems } from './data.js';
import { SampleBase } from '../common/sample-base';
import { FileManagerComponent, Inject, NavigationPane, DetailsView, Toolbar } from '@syncfusion/ej2-react-filemanager';
/**
 * File Manager full functionalities sample
 */
/* import RemoteFileSystemProvider from 'devextreme/file_management/remote_provider';

const remoteProvider = new RemoteFileSystemProvider({
  endpointUrl: 'https://js.devexpress.com/Demos/Mvc/api/file-manager-file-system-scripts'
}); */

/* class App extends React.Component {
  render() {
    return (
      <FileManager fileSystemProvider={fileItems} > {/* pass remote for rfs }
        <Permissions
          create={true}
          copy={true}
          move={true}
          delete={true}
          rename={true}
          upload={true}
          download={true}>
        </Permissions>
      </FileManager>
    );
  }
}

 */

class App extends SampleBase {
    constructor() {
        super(...arguments);
        this.hostUrl = "https://ej2-aspcore-service.azurewebsites.net/";
    }
    render() {
        return (<div>
                <div className="control-section">
                    <FileManagerComponent id="overview_file" ajaxSettings={{
            url: this.hostUrl + "api/FileManager/FileOperations",
            getImageUrl: this.hostUrl + "api/FileManager/GetImage",
            uploadUrl: this.hostUrl + 'api/FileManager/Upload',
            downloadUrl: this.hostUrl + 'api/FileManager/Download'
        }} view={"Details"}>
                <Inject services={[NavigationPane, DetailsView, Toolbar]}/>
                    </FileManagerComponent>
                </div>
            </div>);
    }
}

export default App;
