import React, { useEffect, useState } from 'react'
import QuillEditor from '../../../editor/QuillEditor';
import { Typography, Button, Form, message } from 'antd';
import axios from 'axios';
import { useSelector } from "react-redux";
import './Preview.css';
import ReactQuill from 'react-quill';


const { Title } = Typography;


function CreatePage(props) {
    const user = useSelector(state => state.user);

    const [content, setContent] = useState("")
    const [files, setFiles] = useState([])

    const onEditorChange = (value) => {
        setContent(value)
        console.log(content)
    }

    const onFilesChange = (files) => {
        setFiles(files)
    }

    const [showViews, setShowViews] = useState(false);

    const onSubmit = (event) => {
        event.preventDefault();

        setContent("");

        if (user.userData && !user.userData.isAuth) {
            return alert('Please Log in first');
        }

        const variables = {
            content: content,
            userID: user.userData._id
        }

        axios.post('/api/blog/createPost', variables)
            .then(response => {
                if (response) {
                    message.success('Course Created!');

                    setTimeout(() => {
                        props.history.push('/blog')
                    }, 2000);
                }
            })
    }


    return (
        <React.Fragment>
        <div style={{ maxWidth: '700px', maxHeight:'1400px', display:'inline-block' , margin: '2rem 2rem'}} >
            <div style={{ textAlign: 'center' }}>
                <Title level={2} > Editor </Title>
            </div>
            <QuillEditor
                placeholder={"Start creating your course...."}
                onEditorChange={onEditorChange}
                onFilesChange={onFilesChange}
            />

            <Form onSubmit={onSubmit}>
                <div style={{ textAlign: 'center', margin: '2rem', }}>
                    <Button
                        size="large"
                        htmlType="submit"
                        className=""
                        onSubmit={onSubmit}
                    >
                        Save
                </Button>
                </div>
            </Form>

            <div style={{ textAlign: 'center', margin: '2rem', }}>
                    <Button
                        size="large"
                        htmlType="submit"
                        onClick={()=>setShowViews(true)}
                    >
                        Views
                </Button>
                </div>

                {
            showViews && (
                <div>

                    <Title level={3} > Mobile View </Title>  
                    <div class="mv" dangerouslySetInnerHTML={{ __html:content }} />
                        <br />
                    <center>
                    <Title level={3} > Tablet View.... </Title>     
                    <div class="tv" dangerouslySetInnerHTML={{ __html:content }} />
                    </center>
                </div>
          )
      }
            </div>

            <div class="mv"  dangerouslySetInnerHTML={{ __html:content }} />
      </React.Fragment>


    )
}

export default CreatePage;
