import React, { useEffect, useState } from 'react'
import QuillEditor from '../../../editor/QuillEditor';
import { Typography, Button, Form, message } from 'antd';
import axios from 'axios';
import { useSelector } from "react-redux";
import './Preview.css';
import ReactQuill from 'react-quill';


const { Title } = Typography;


function CreatePage(props) {
    const user = useSelector(state => state.user);

    const [content, setContent] = useState("")
    const [files, setFiles] = useState([])

    const onEditorChange = (value) => {
        setContent(value)
        console.log(content)
    }

    const onFilesChange = (files) => {
        setFiles(files)
    }

    const [showViews, setShowViews] = useState(false);

    const onSubmit = (event) => {
        event.preventDefault();

        setContent("");

        if (user.userData && !user.userData.isAuth) {
            return alert('Please Log in first');
        }

        const variables = {
            content: content,
            userID: user.userData._id
        }

        axios.post('/api/blog/createPost', variables)
            .then(response => {
                if (response) {
                    message.success('Course Created!');

                    setTimeout(() => {
                        props.history.push('/blog')
                    }, 2000);
                }
            })
    }


    return (

        <React.Fragment>
            {/* BLOCK 1 */}
            <div style={{ maxWidth: '700px', display:'inline-block' , margin: '2rem', verticalAlign:'top'}} >

                    <div style={{ textAlign: 'center' }}>
                        <Title level={2} > Editor </Title>
                    </div>

                    <QuillEditor
                        placeholder={"Start creating your course...."}
                        onEditorChange={onEditorChange}
                        onFilesChange={onFilesChange}
                    />

                    <Form onSubmit={onSubmit}>
                        <div style={{ textAlign: 'center', margin: '2rem', }}>
                            <Button
                                size="large"
                                htmlType="submit"
                                className=""
                                onSubmit={onSubmit}
                            >
                                Save
                            </Button>
                        </div>
                    </Form>

                    <div style={{ textAlign: 'center', margin: '2rem', }}>
                            <Button
                                size="large"
                                htmlType="submit"
                                onClick={()=>setShowViews(true)}
                            >
                                Views
                        </Button>
                    </div>

            </div>

            {/* BLOCK 2 */}

            {
                showViews && (
                    <React.Fragment>    
                        <div style={{display:'inline-block'}}>
                            <center><Title level={3} > Mobile View </Title></center>  
                            <div class="mv"  >
                                <div dangerouslySetInnerHTML={{ __html:content }} >

                                </div>
                                </div>
                        </div>

                        <center><div>
                            <center><Title level={3} > Tablet View </Title></center>     
                            <div class="tv" dangerouslySetInnerHTML={{ __html:content }} />
                        </div></center>
                    </React.Fragment>
                )
            }


      </React.Fragment>


    )
}

export default CreatePage;
